// TodoList.js

import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { addTodo, toggleTodo, addTodosFromApi } from "./actions";
import axios from "axios"; // Import Axios
import "./TodoList.css";

const TodoList = () => {
  const todos = useSelector((state) => state.todos);
  const dispatch = useDispatch();
  const [newTodo, setNewTodo] = useState("");

  // Fetch data from API
  useEffect(() => {
    axios
      .get("https://65410d1945bedb25bfc326b0.mockapi.io/api/v1/todos")
      .then((response) => {
        const apiTodos = response.data.map((item) => ({
          text: item.name,
          completed: false,
        }));
        dispatch(addTodosFromApi(apiTodos)); // Dispatch action to add todos from API
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, [dispatch]);

  const handleInputChange = (e) => {
    setNewTodo(e.target.value);
  };

  const handleAddTodo = () => {
    if (newTodo.trim() !== "") {
      dispatch(addTodo(newTodo));
      setNewTodo("");
    }
  };

  const handleToggleTodo = (index) => {
    dispatch(toggleTodo(index));
  };

  return (
    <div>
      <h1>TODO LIST</h1>
      <ul>
        {todos.map((todo, index) => (
          <li
            key={index}
            style={{ textDecoration: todo.completed ? "line-through" : "none" }}
            onClick={() => handleToggleTodo(index)}
          >
            {todo.text}
          </li>
        ))}
      </ul>
      <div className="new-note">
        <input
          type="text"
          value={newTodo}
          onChange={handleInputChange}
          placeholder="New note..."
        />
        <button onClick={handleAddTodo}>ADD</button>
      </div>
    </div>
  );
};

export default TodoList;
